mapper.update:
update jsh_cashier_session_product_item
session_id = #{sessionId,jdbcType=BIGINT}, material_id = #{materialId,jdbcType=BIGINT}, material_name_snap = #{materialNameSnap,jdbcType=VARCHAR}, unit_price = #{unitPrice,jdbcType=DECIMAL}, qty = #{qty,jdbcType=DECIMAL}, amount = #{amount,jdbcType=DECIMAL}, create_time = #{createTime,jdbcType=TIMESTAMP}, tenant_id = #{tenantId,jdbcType=BIGINT}, delete_flag = #{deleteFlag,jdbcType=VARCHAR},
where id = #{id,jdbcType=BIGINT}

mapper.select:
select *
from jsh_cashier_session_product_item
where session_id = #{sessionId}
  and material_id = #{materialId}
  and ifnull(delete_flag,'0') != '1'

    and tenant_id = #{tenantId}

order by id desc
limit 1

