mapper.update:
update jsh_cashier_settlement
settlement_no = #{settlementNo,jdbcType=VARCHAR}, session_id = #{sessionId,jdbcType=BIGINT}, depot_id = #{depotId,jdbcType=BIGINT}, seat_id = #{seatId,jdbcType=BIGINT}, member_id = #{memberId,jdbcType=BIGINT}, service_total_amount = #{serviceTotalAmount,jdbcType=DECIMAL}, product_total_amount = #{productTotalAmount,jdbcType=DECIMAL}, total_amount = #{totalAmount,jdbcType=DECIMAL}, real_pay_amount = #{realPayAmount,jdbcType=DECIMAL}, change_amount = #{changeAmount,jdbcType=DECIMAL}, discount_amount = #{discountAmount,jdbcType=DECIMAL}, items_json = #{itemsJson,jdbcType=LONGVARCHAR}, status = #{status,jdbcType=VARCHAR}, cashier_user_id = #{cashierUserId,jdbcType=BIGINT}, created_time = #{createdTime,jdbcType=TIMESTAMP}, remark = #{remark,jdbcType=VARCHAR}, tenant_id = #{tenantId,jdbcType=BIGINT}, delete_flag = #{deleteFlag,jdbcType=VARCHAR},
where id = #{id,jdbcType=BIGINT}

mapper.select:
select * from jsh_cashier_settlement where session_id = #{sessionId} and ifnull(delete_flag,'0') != '1'
and tenant_id = #{tenantId}
order by created_time desc, id desc limit 1

select *
from jsh_cashier_settlement
where ifnull(delete_flag,'0') != '1'

    and tenant_id = #{tenantId}


    and depot_id = #{depotId}


    and created_time >= #{startTime}


    and created_time <= #{endTime}

order by created_time desc, id desc

  limit #{offset},#{rows}
