mapper.update:
update jsh_cashier_settlement_payment
settlement_id = #{settlementId,jdbcType=BIGINT}, pay_method = #{payMethod,jdbcType=VARCHAR}, amount = #{amount,jdbcType=DECIMAL}, tenant_id = #{tenantId,jdbcType=BIGINT}, delete_flag = #{deleteFlag,jdbcType=VARCHAR},
where id = #{id,jdbcType=BIGINT}

mapper.select:
select *
from jsh_cashier_settlement_payment
where settlement_id = #{settlementId}
  and ifnull(delete_flag,'0') != '1'

    and tenant_id = #{tenantId}

order by id asc

mapper.delete:
delete from jsh_cashier_settlement_payment
where settlement_id = #{settlementId}

    and tenant_id = #{tenantId}
