mapper.update:
update jsh_cashier_shift
depot_id = #{depotId,jdbcType=BIGINT}, cashier_user_id = #{cashierUserId,jdbcType=BIGINT}, start_time = #{startTime,jdbcType=TIMESTAMP}, end_time = #{endTime,jdbcType=TIMESTAMP}, opening_amount = #{openingAmount,jdbcType=DECIMAL}, closing_amount = #{closingAmount,jdbcType=DECIMAL}, total_amount = #{totalAmount,jdbcType=DECIMAL}, diff_amount = #{diffAmount,jdbcType=DECIMAL}, remark = #{remark,jdbcType=VARCHAR}, tenant_id = #{tenantId,jdbcType=BIGINT}, delete_flag = #{deleteFlag,jdbcType=VARCHAR},
where id = #{id,jdbcType=BIGINT}

mapper.select:
select *
from jsh_cashier_shift
where depot_id = #{depotId}
  and cashier_user_id = #{cashierUserId}
  and end_time is null
  and ifnull(delete_flag,'0') != '1'

    and tenant_id = #{tenantId}

order by start_time desc, id desc
limit 1
