mapper.update:
update jsh_credit_bill
session_id = #{sessionId,jdbcType=BIGINT}, member_id = #{memberId,jdbcType=BIGINT}, amount = #{amount,jdbcType=DECIMAL}, status = #{status,jdbcType=VARCHAR}, created_time = #{createdTime,jdbcType=TIMESTAMP}, settled_time = #{settledTime,jdbcType=TIMESTAMP}, tenant_id = #{tenantId,jdbcType=BIGINT}, remark = #{remark,jdbcType=VARCHAR}, delete_flag = #{deleteFlag,jdbcType=VARCHAR},
where id = #{id,jdbcType=BIGINT}

mapper.select:
select *
from jsh_credit_bill
where status = #{status}
  and ifnull(delete_flag,'0') != '1'

    and tenant_id = #{tenantId}

order by created_time desc, id desc

select b.*
from jsh_credit_bill b
  left join jsh_cashier_session s on s.id = b.session_id
where b.status = #{status}
  and ifnull(b.delete_flag,'0') != '1'
  and ifnull(s.delete_flag,'0') != '1'

    and b.tenant_id = #{tenantId}


    and s.depot_id in

      #{id}


order by b.created_time desc, b.id desc

select b.*
from jsh_credit_bill b
  left join jsh_cashier_session s on s.id = b.session_id
where 1=1

    and s.depot_id = #{depotId}


    and b.created_time >= #{startTime}


    and b.created_time <= #{endTime}

  and ifnull(b.delete_flag,'0') != '1'
  and ifnull(s.delete_flag,'0') != '1'

    and b.tenant_id = #{tenantId}

order by b.created_time desc, b.id desc
