mapper.update:
update jsh_invoice_request
settlement_id = #{settlementId,jdbcType=BIGINT}, session_id = #{sessionId,jdbcType=BIGINT}, depot_id = #{depotId,jdbcType=BIGINT}, member_id = #{memberId,jdbcType=BIGINT}, buyer_type = #{buyerType,jdbcType=VARCHAR}, buyer_name = #{buyerName,jdbcType=VARCHAR}, tax_no = #{taxNo,jdbcType=VARCHAR}, email = #{email,jdbcType=VARCHAR}, phone = #{phone,jdbcType=VARCHAR}, invoice_type = #{invoiceType,jdbcType=VARCHAR}, invoice_content = #{invoiceContent,jdbcType=VARCHAR}, amount = #{amount,jdbcType=DECIMAL}, status = #{status,jdbcType=VARCHAR}, invoice_no = #{invoiceNo,jdbcType=VARCHAR}, file_url = #{fileUrl,jdbcType=VARCHAR}, created_time = #{createdTime,jdbcType=TIMESTAMP}, processed_time = #{processedTime,jdbcType=TIMESTAMP}, processor_user_id = #{processorUserId,jdbcType=BIGINT}, remark = #{remark,jdbcType=VARCHAR}, tenant_id = #{tenantId,jdbcType=BIGINT}, delete_flag = #{deleteFlag,jdbcType=VARCHAR},
where id = #{id,jdbcType=BIGINT}

mapper.select:
select *
from jsh_invoice_request
where ifnull(delete_flag,'0') != '1'

    and tenant_id = #{tenantId}


    and depot_id = #{depotId}


    and status = #{status}


    and (buyer_name like concat('%',#{keyword},'%') or tax_no like concat('%',#{keyword},'%') or email like concat('%',#{keyword},'%'))

order by created_time desc, id desc

  limit #{offset},#{rows}

select count(1)
from jsh_invoice_request
where ifnull(delete_flag,'0') != '1'

    and tenant_id = #{tenantId}


    and depot_id = #{depotId}


    and status = #{status}


    and (buyer_name like concat('%',#{keyword},'%') or tax_no like concat('%',#{keyword},'%') or email like concat('%',#{keyword},'%'))
