mapper.select:
select *
from jsh_service_item
where 1=1

    and name like concat('%', #{name}, '%')


    and enabled = #{enabled}

  and ifnull(delete_flag,'0') != '1'

    and tenant_id = #{tenantId}

order by sort asc, id asc

  limit #{offset},#{rows}

select count(id) from jsh_service_item where 1=1
and name like concat('%', #{name}, '%')
and enabled = #{enabled}
and ifnull(delete_flag,'0') != '1'
and tenant_id = #{tenantId}
