mapper.update:
update jsh_service_order_item
service_order_id = #{serviceOrderId,jdbcType=BIGINT}, service_item_id = #{serviceItemId,jdbcType=BIGINT}, qty = #{qty,jdbcType=DECIMAL}, unit_price = #{unitPrice,jdbcType=DECIMAL}, amount = #{amount,jdbcType=DECIMAL}, commission_amount = #{commissionAmount,jdbcType=DECIMAL}, tenant_id = #{tenantId,jdbcType=BIGINT}, delete_flag = #{deleteFlag,jdbcType=VARCHAR},
where id = #{id,jdbcType=BIGINT}

mapper.select:
select *
from jsh_service_order_item
where service_order_id = #{serviceOrderId}
  and ifnull(delete_flag,'0') != '1'

    and tenant_id = #{tenantId}

order by id asc

select sum(i.amount) as totalAmount, sum(i.commission_amount) as totalCommission from jsh_service_order_item i left join jsh_service_order o on o.id = i.service_order_id left join jsh_cashier_session s on s.id = o.session_id where 1=1
and s.depot_id = #{depotId}
and o.created_time >= #{startTime}
and o.created_time <= #{endTime}
and ifnull(i.delete_flag,'0') != '1' and ifnull(o.delete_flag,'0') != '1' and ifnull(s.delete_flag,'0') != '1'
and i.tenant_id = #{tenantId}
