mapper.update:
update jsh_service_order
session_id = #{sessionId,jdbcType=BIGINT}, technician_id = #{technicianId,jdbcType=BIGINT}, status = #{status,jdbcType=VARCHAR}, created_time = #{createdTime,jdbcType=TIMESTAMP}, finished_time = #{finishedTime,jdbcType=TIMESTAMP}, tenant_id = #{tenantId,jdbcType=BIGINT}, remark = #{remark,jdbcType=VARCHAR}, delete_flag = #{deleteFlag,jdbcType=VARCHAR},
where id = #{id,jdbcType=BIGINT}

mapper.select:
select *
from jsh_service_order
where session_id = #{sessionId}
  and ifnull(delete_flag,'0') != '1'

    and tenant_id = #{tenantId}

order by created_time desc, id desc

select * from jsh_service_order where session_id = #{sessionId} and status = 'OPEN' and technician_id is null and ifnull(delete_flag,'0') != '1'
and tenant_id = #{tenantId}
order by created_time desc, id desc limit 1
